import React, { useEffect, useRef, useState } from 'react'
import { storage } from '../firebase/firebase-config';
import { v4 as uuidv4 } from 'uuid'
const axios = require('axios').default;

const Main = () => {

    const fileInputRef = useRef();

    const [image, setImage] = useState();
    const [preview, setPreview] = useState();
    const [imageName, setImageName] = useState('');
    const [transfered, setTransfered] = useState(0)
    const [uploading, setUploading] = useState(false);
    const [transferedFinished, setTransferedFinished] = useState(false)
    const [result, setResult] = useState();

    const handleChangeFile = (event) => {
        event.preventDefault();

        const file = event.target.files[0];

        if (file && file.type.substr(0, 5) === 'image') {
            setImage(file)
        } else {
            console.log('No es imagen')
            setImage(null);
        }
    }

    const handleFile = (image) => {
        const reader = new FileReader();
        reader.onloadend = () => {
            setPreview(reader.result);
        }
        reader.readAsDataURL(image);
    }

    const handleFirebaseUpload = async () => {

        console.log('start of upload...')

        if (image) {
            setUploading(true);
            setTransfered(0);

            const name = uuidv4();
            const filename = name + '.jpg';

            const storageRef = storage.ref(`uploaded/${filename}`);
            const task = storageRef.put(image);

            task.on('state_changed', (taskSnapshot) => {
                console.log(`${taskSnapshot.bytesTransferred} transfered out of ${taskSnapshot.totalBytes}`);

                setTransfered(
                    Math.round(taskSnapshot.bytesTransferred / taskSnapshot.totalBytes) * 100
                )
            })


            setImageName(name);

            try {
                await task;
                const url = await storageRef.getDownloadURL();
                setUploading(false);
                setTransferedFinished(true);
                return url;
            } catch (error) {
                console.log('Error downloading the image url')
                return null;
            }

        }
    }

    const handleUpload = async (e) => {
        e.preventDefault();

        let imgUrl = await handleFirebaseUpload();

        if (imgUrl) {
            console.log('ImgUrl', imgUrl)
        }
    }

    const handlePrediction = () => {

        axios.post('https://ai-backend-project.herokuapp.com/detectFaces', {
            imageName: imageName.toString()
        })
            .then((res) => {
                console.log(res.data.imageURL)
                setResult(res.data);
            })
            .catch((e) => console.log('Prediciont Failed', e))

    }

    useEffect(() => {
        if (image) {
            handleFile(image)
        } else {
            setPreview(null)
        }
    }, [image]);


    return (
        <div className="container">
            <div className="image-container">
                <div className="image-container-1">
                    <div className="card" style={{ width: "18rem" }}>
                        <img className="card-img-top" src={preview ? preview : 'https://via.placeholder.com/600/d32776'} alt="preview" />
                        <div className="card-body">
                            <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        </div>
                    </div>
                    {
                        preview ? (
                            <button onClick={() => { setImage(null) }}>Quitar Imagen</button>
                        ) : (
                            <button onClick={(event) => {
                                event.preventDefault();
                                fileInputRef.current.click();
                            }}>
                                Abrir Imagen
                            </button>
                        )
                    }
                </div>
                <div className="button-container">
                    <button className="button-prediction" onClick={(e) => handleUpload(e)}>Predecir</button>
                </div>

                <div className="image-container-2">
                    <div className="card" style={{ width: "18rem" }}>
                        <img className="card-img-top" src={result ? result.imageURL : 'https://via.placeholder.com/600/d32776'} alt="preview" />
                        <div className="card-body">
                            <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        </div>
                    </div>
                </div>
            </div>

            <input
                className="file-button"
                style={{ display: 'none' }}
                type="file"
                ref={fileInputRef}
                accept="image/"
                onChange={(event) => { handleChangeFile(event); }}
            />


            <span>Porcentaje: {transfered}</span>
            {
                transferedFinished ? (
                    <button onClick={() => handlePrediction()}>Predecir</button>
                ) : null
            }
        </div>
    )
}

export default Main
