import React, { useEffect, useRef, useState } from 'react'

const Main = () => {

    const fileInputRef = useRef();

    const [image, setImage] = useState();
    const [preview, setPreview] = useState();
    const [transfered, setTransfered] = useState(0)
    const [uploading, setUploading] = useState(false);

    const handleChangeFile = (event) => {
        event.preventDefault();

        const file = event.target.files[0];
        if (file && file.type.substr(0, 5) === 'image') {
            setImage(file)
        } else {
            console.log('No es imagen')
            setImage(null);
        }
    }

    const handleFile = (image) => {
        const reader = new FileReader();
        reader.onloadend = () => {
            setPreview(reader.result);
        }
        reader.readAsDataURL(image);
    }

    const handleFirebaseUpload = async (e) => {
        e.preventDefault();
        console.log('start of upload...')
        if (image) {
            setUploading(true);
            setTransfered(0);

            const filename = uuid();
            // const uploadTask = storage.ref(`/images/${Image.name}`).put(Image)
            const storageRef = storage().ref(`uploaded/${filename}`);
            const task = storageRef.putFile(image);

            task.on('state_changed', (taskSnapshot) => {
                console.log(`${taskSnapshot.bytesTransferred} transfered out of ${taskSnapshot.totalBytes}`);

                setTransfered(
                    Math.round(taskSnapshot.bytesTransferred / taskSnapshot.totalBytes) * 100
                )
            })

            //Obtener el url
            try {
                await task;

                const url = await storageRef.getDownloadURL();
                setUploading(false);
                // setImage(null) no necesariamente
                return url; //asignar este metodo para tener el url
            } catch (error) {
                console.log('Error downloading the image url')
                return null;
            }

        }
    }

    useEffect(() => {
        if (image) {
            handleFile(image)
        } else {
            setPreview(null)
        }
    }, [image]);


    return (
        <>
            <div>
                Main
        </div>

            <input
                className="file-button"
                style={{ display: 'none' }}
                type="file"
                ref={fileInputRef}
                accept="image/"
                onChange={(event) => { handleChangeFile(event); }}
            />
            {
                preview ? (
                    <>
                        <img src={preview} alt={"preview"} style={{ objectFit: 'cover' }} />
                        <button onClick={() => { setImage(null) }}>Quitar Imagen</button>
                    </>
                ) : (
                    <button onClick={(event) => {
                        event.preventDefault();
                        fileInputRef.current.click();
                    }}>
                        Abrir Imagen
                    </button>
                )
            }
            <button onClick={(e) => handleFirebaseUpload(e)}>Cargar a Firebase</button>
        </>
    )
}

export default Main
